#!/bin/bash
# Create a test image
echo "Creating test image..."
convert -size 100x100 xc:red /tmp/test-image.jpg 2>/dev/null || {
  # If ImageMagick is not available, create a simple base64 image
  echo "Using base64 test image..."
  echo "" | base64 -d > /tmp/test-image.jpg
}

# Upload the test image
echo "Uploading test image to http://localhost:3000/api/image-upload..."
curl -X POST http://localhost:3000/api/image-upload \
  -F "file=@/tmp/test-image.jpg" \
  -F "manhole_id=1" \
  -F "shot_at=$(date -u +%Y-%m-%dT%H:%M:%S.000Z)" \
  -F "latitude=35.6812" \
  -F "longitude=139.7671" \
  -F "note=Test upload" \
  2>&1 | jq '.' || echo "Failed to parse JSON response"

# Clean up
rm -f /tmp/test-image.jpg
